    /**
     * Mengambil jadwal sholat untuk hari ini berdasarkan tanggal dan bulan.
     */
    public function getJadwalHariIni(): JsonResponse
    {
        Carbon::setLocale('id');
        date_default_timezone_set('Asia/Jakarta');

        $sekarang = Carbon::now();
        $tanggal = $sekarang->format('d'); 
        $bulan = $sekarang->translatedFormat('F'); 

        // Ambil semua data jadwal
        $jadwalLengkap = $this->getAllJadwal();

        if (isset($jadwalLengkap[$bulan]) && isset($jadwalLengkap[$bulan][$tanggal])) {
            $jadwalHariIni = $jadwalLengkap[$bulan][$tanggal];
            $jadwalHariIni['tanggal'] = $tanggal;
            $jadwalHariIni['bulan'] = $bulan;
            
            return response()->json($jadwalHariIni);
        }

        return response()->json(['error' => 'Jadwal sholat tidak ditemukan untuk hari ini.'], 404);
    }

